package problem_solving;

import static org.junit.Assert.assertEquals;

import org.junit.Test;

public class TwoTwoTest {
	
	@Test
	public void twoTwoTest() {
		assertEquals(4, TwoTwo.twoTwo("24256"));
	}

	
	@Test
	public void twoTwoOnlyTwoTest() {
		assertEquals(7, TwoTwo.twoTwo("2222222"));
	}
	
	@Test
	public void twoTwoNoPowersOfTwoTest() {
		assertEquals(0, TwoTwo.twoTwo("3597715"));
	}

	@Test
	public void twoTwoNoHugeNumberTest() {
		assertEquals(358, TwoTwo.twoTwo(""));
	}

	@Test
	public void twoTwoWithZeroTest() {
		assertEquals(4, TwoTwo.twoTwo("023223"));
	}
	
}
